/**
 * Plugin star item resource object representing a plugin star.
 */
export class PluginStar extends ItemResource {
  /**
   * Constructor
   *
   * @param {string} url - url of the resource
   * @param {Object} [auth=null] - authentication object
   * @param {string} [auth.token] - authentication token
   */
  constructor(url, auth = null) {
    super(url, auth);
  }

  /**
   * Fetch the plugin meta associated to this plugin star from the REST API.
   *
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to a ``PluginMeta`` object
   */
  getPluginMeta(timeout = 30000) {
    const linkRelation = 'meta';
    const resourceClass = PluginMeta;

    return this._getResource(linkRelation, resourceClass, null, timeout);
  }

  /**
   * Fetch the user associated to this plugin star from the REST API.
   *
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to a ``User`` object
   */
  getUser(timeout = 30000) {
    const linkRelation = 'user';
    const resourceClass = User;

    return this._getResource(linkRelation, resourceClass, null, timeout);
  }

  /**
   * Make a DELETE request to delete this plugin star item resource through the
   * REST API.
   *
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise
   */
  delete(timeout = 30000) {
    return this._delete(timeout);
  }
}

/**
 * Plugin star list resource object representing a list of plugin stars.
 */
export class PluginStarList extends ListResource {
  /**
   * Constructor
   *
   * @param {string} url - url of the resource
   * @param {Object} [auth=null] - authentication object
   * @param {string} [auth.token] - authentication token
   */
  constructor(url, auth = null) {
    super(url, auth);

    /** @type {Object} */
    this.itemClass = PluginStar;
  }

  /**
   * Fetch a list of plugins from the REST API.
   *
   * @param {Object} [searchParams=null] - search parameters object which is
   * resource-specific, the ``PluginList.getSearchParameters`` method
   * can be used to get a list of possible search parameters
   * @param {number} [searchParams.limit] - page limit
   * @param {number} [searchParams.offset] - page offset
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to a ``PluginList`` object
   */
  getPlugins(searchParams = null, timeout = 30000) {
    const linkRelation = 'plugins';
    const resourceClass = PluginList;

    return this._getResource(linkRelation, resourceClass, searchParams, timeout);
  }

  /**
   * Make a POST request to this plugin star list resource to create a new
   * plugin star item resource through the REST API.
   *
   * @param {Object} data - request JSON data object
   * @param {string} data.plugin_name - plugin name
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  post(data, timeout = 30000) {
    return this._post(data, null, timeout);
  }
}

/** * Imports ***/
import axios from 'axios';
import Collection from './cj';
import RequestException from './exception';

/**
 * Http request object.
 */
export default class Request {
  /**
   * Constructor
   *
   * @param {Object} auth - authentication object
   * @param {string} auth.token - authentication token
   * @param {string} contentType - request content type
   * @param {number} [timeout=30000] - request timeout
   */
  constructor(auth, contentType, timeout = 30000) {
    /** @type {Object} */
    this.auth = auth;

    /** @type {string} */
    this.contentType = contentType;

    /** @type {number} */
    this.timeout = timeout;
  }

  /**
   * Perform a GET request.
   *
   * @param {string} url - url of the resource
   * @param {?Object} params - search parameters
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  get(url, params = null) {
    const config = this._getConfig(url, 'get');

    if (params) {
      config.params = params;
    }

    return Request._callAxios(config);
  }

  /**
   * Perform a POST request.
   *
   * @param {string} url - url of the resource
   * @param {Object} data - JSON data object
   * @param {?Object} uploadFileObj - custom object with a property with the same name as
   * the API descriptor corresponding to the file and whose value is the file blob
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  post(url, data, uploadFileObj = null) {
    return this._postOrPut('post', url, data, uploadFileObj);
  }

  /**
   * Perform a PUT request.
   *
   * @param {string} url - url of the resource
   * @param {Object} data - JSON data object
   * @param {?Object} uploadFileObj - custom object with a property with the same name as
   * the API descriptor corresponding to the file and whose value is the file blob
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  put(url, data, uploadFileObj = null) {
    return this._postOrPut('put', url, data, uploadFileObj);
  }

  /**
   * Perform a DELETE request.
   *
   * @param {string} url - url of the resource
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  delete(url) {
    const config = this._getConfig(url, 'delete');

    return Request._callAxios(config);
  }

  /**
   * Internal method to make either a POST or PUT request.
   *
   * @param {string} requestMethod - either 'post' or 'put'
   * @param {string} url - url of the resource
   * @param {Object} data - JSON data object
   * @param {?Object} uploadFileObj - custom object with a property with the same name as
   * the API descriptor corresponding to the file and whose value is the file blob
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  _postOrPut(requestMethod, url, data, uploadFileObj = null) {
    const config = this._getConfig(url, requestMethod);
    config.data = data;

    if (uploadFileObj) {
      config['headers']['content-type'] = 'multipart/form-data';
      const bFormData = new FormData();

      for (let property in data) {
        if (data.hasOwnProperty(property)) {
          bFormData.set(property, data[property]);
        }
      }
      for (let property in uploadFileObj) {
        if (uploadFileObj.hasOwnProperty(property)) {
          bFormData.set(property, uploadFileObj[property]);
        }
      }
      config.data = bFormData;
    }

    return Request._callAxios(config);
  }

  /**
   * Internal method to create a config file for axios.
   *
   * @param {string} url - url of the resource
   * @param {string} method - request verb
   *
   * @return {Object} - axios configuration object
   */
  _getConfig(url, method) {
    const config = {
      url: url,
      method: method,
      headers: {
        Accept: this.contentType,
        'content-type': this.contentType,
      },
      timeout: this.timeout,
    };

    if (this.auth && this.auth.username && this.auth.password) {
      config.auth = this.auth;
    } else if (this.auth && this.auth.token) {
      config.headers.Authorization = 'Token ' + this.auth.token;
    }

    if (this.contentType === 'application/octet-stream') {
      config.responseType = 'blob';
    }

    return config;
  }

  /**
   * Internal method to make an axios request.
   *
   * @param {Object} config - axios configuration object
   *
   * @return {Object} - JS Promise, resolves to an ``axios reponse`` object
   */
  static _callAxios(config) {
    return axios(config)
      .then(response => {
        return response;
      })
      .catch(error => {
        Request._handleRequestError(error);
      });
  }

  /**
   * Internal method to handle errors produced by HTTP requests.
   *
   * @param {Object} error - axios error object
   *
   * @throws {RequestException} throw error
   */
  static _handleRequestError(error) {
    let apiError;

    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      //console.log(error.response.data);
      //console.log(error.response.status);
      //console.log(error.response.headers);
      let errMsg = 'Bad server response!';
      if (error.response.data.collection) {
        errMsg = Collection.getErrorMessage(error.response.data.collection);
      }
      apiError = new RequestException(errMsg);
      apiError.request = error.request;
      apiError.response = error.response;
      try {
        apiError.response.data = JSON.parse(errMsg);
      } catch (ex) {
        apiError.response.data = errMsg;
      }
    } else if (error.request) {
      // The request was made but no response was received
      // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
      // http.ClientRequest in node.js
      //console.log(error.request);
      apiError = new RequestException('No server response!');
      apiError.request = error.request;
    } else {
      // Something happened in setting up the request that triggered an Error
      //console.log('Error', error.message);
      apiError = new RequestException(error.message);
    }

    throw apiError;
    //console.log(error.config);
  }

  /**
   * Helper method to run an asynchronous task defined by a task generator function.
   *
   * @param {function*()} taskGenerator - generator function
   */
  static runAsyncTask(taskGenerator) {
    // create the iterator
    let task = taskGenerator();
    // start the task
    let result = task.next();

    // recursive function to iterate through
    (function step() {
      // if there's more to do (result.value and result.done are iterator's properties)
      if (!result.done) {
        result.value
          .then(resp => {
            result = task.next(resp); // send this resp value to the yield
            step();
          })
          .catch(error => {
            result = task.throw(error); // throws error within taskGenerator generator
            step();
          });
      }
    })(); // start the recursive process by calling it immediatly
  }
}

/** * Imports ***/
import Collection from './cj';
import Request from './request';
import RequestException from './exception';

/**
 * API abstract resource class.
 */
export class Resource {
  /**
   * Constructor
   *
   * @param {string} resourceUrl - url of the resource
   * @param {Object} [auth=null] - authentication object
   * @param {string} [auth.token] - authentication token
   */
  constructor(resourceUrl, auth = null) {
    /** @type {string} */
    this.url = resourceUrl;

    /** @type {Object} */
    this.auth = auth;

    /** @type {string} */
    this.contentType = 'application/vnd.collection+json';

    /** @type {?Object} */
    this.collection = null; // Collection+JSON collection obj
  }

  /**
   * Return true if the resource object contains any data.
   *
   * @type {boolean}
   */
  get isEmpty() {
    if (this.collection && this.collection.items.length) {
      return false;
    }
    return true;
  }

  /**
   * Make a deep copy clone of this object resource.
   *
   * @return {Object} - clone object
   */
  clone() {
    const cloneObj = Object.create(Object.getPrototypeOf(this));

    for (let prop in this) {
      if (this[prop] !== null && typeof this[prop] === 'object') {
        cloneObj[prop] = JSON.parse(JSON.stringify(this[prop]));
      } else {
        cloneObj[prop] = this[prop];
      }
    }
    return cloneObj;
  }
}

/**
 * API abstract item resource class.
 */
export class ItemResource extends Resource {
  /**
   * Constructor
   *
   * @param {string} itemUrl - url of the resource
   * @param {Object} [auth=null] - authentication object
   * @param {string} [auth.token] - authentication token
   */
  constructor(itemUrl, auth = null) {
    super(itemUrl, auth);
  }

  /**
   * Fetch this item resource from the REST API.
   *
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  get(timeout = 30000) {
    const req = new Request(this.auth, this.contentType, timeout);

    return req.get(this.url).then(resp => {
      // change the state of this object on successfull response
      this.collection = null;
      if (resp.data && resp.data.collection) {
        this.collection = resp.data.collection;
      }
      return this;
    });
  }

  /**
   * Get the item's data object (REST API descriptors).
   *
   * @type {?Object}
   */
  get data() {
    if (this.isEmpty) {
      return null;
    }
    return Collection.getItemDescriptors(this.collection.items[0]);
  }

  /**
   * Get an array of parameter names that can be used as properties of the data
   * object in PUT requests.
   *
   * @return {?string[]} - array of PUT data property name or null if this list
   * resource's data has not been fetched from the API yet or it doesn't support
   * PUT requests.
   */
  getPUTParameters() {
    if (this.collection && this.collection.template) {
      return Collection.getTemplateDescriptorNames(this.collection.template);
    }
    return null;
  }

  /**
   * Internal method to fetch a related resource from the REST API that is referenced
   * by a link relation within the item object.
   *
   * @param {string} linkRelation
   * @param {Object} ResourceClass
   * @param {Object} [searchParams=null] - search parameters object which is resource-specific
   * @param {number} [searchParams.limit] - page limit
   * @param {number} [searchParams.offset] - page offset
   * @param {number} [timeout=30000] - request timeout
   * @return {Object} - JS Promise, resolves to a ``ResourceClass`` object
   * @throws {RequestException} throw error if this item resource has not yet been
   * fetched from the REST API
   * @throws {RequestException} throw error when the link relation is not found
   */
  _getResource(linkRelation, ResourceClass, searchParams = null, timeout = 30000) {
    if (this.isEmpty) {
      throw new RequestException('Item object has not been set!');
    }
    const item = this.collection.items[0];
    const urls = Collection.getLinkRelationUrls(item, linkRelation);

    if (!urls.length) {
      const errMsg = 'Missing "' + linkRelation + '" link relation!';
      throw new RequestException(errMsg);
    }
    const resourceUrl = urls[0];
    const resourceObj = new ResourceClass(resourceUrl, this.auth);
    if (searchParams) {
      return resourceObj.get(searchParams, timeout);
    }
    return resourceObj.get(timeout);
  }

  /**
   * Internal helper method to make a PUT request to this item resource through
   * the REST API.
   *
   * @param {Object} data - request JSON data object
   * @param {?Object} uploadFileObj - custom file object
   * @param {Object} uploadFileObj.fname - file blob
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  _put(data, uploadFileObj, timeout = 30000) {
    const req = new Request(this.auth, this.contentType, timeout);
    let putData = data;

    if (!uploadFileObj && this.contentType === 'application/vnd.collection+json') {
      putData = { template: Collection.makeTemplate(data) };
    }
    return req.put(this.url, putData, uploadFileObj).then(resp => {
      // change the state of this object on successfull response
      this.collection = null;
      if (resp.data && resp.data.collection) {
        this.collection = resp.data.collection;
      }
      return this;
    });
  }

  /**
   * Internal helper method to make a DELETE request to this item resource through
   * the REST API.
   *
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise
   */
  _delete(timeout = 30000) {
    const req = new Request(this.auth, this.contentType, timeout);

    return req.delete(this.url).then(() => {
      // change the state of this object on successfull response
      this.collection = null;
    });
  }
}

/**
 * API abstract list resource class.
 */
export class ListResource extends Resource {
  /**
   * Constructor
   *
   * @param {string} listUrl - url of the resource
   * @param {Object} [auth=null] - authentication object
   * @param {string} [auth.token] - authentication token
   */
  constructor(listUrl, auth = null) {
    super(listUrl, auth);

    /** @type {string} */
    this.queryUrl = '';

    /** @type {?Object} */
    this.searchParams = null;

    /** @type {Object} */
    this.itemClass = ItemResource;
  }

  /**
   * Fetch this list resource from the REST API based on search parameters. If
   * no search parameters then get the default first page.
   *
   * @param {Object} [searchParams=null] - search parameters object which is
   * resource-specific, the ``getSearchParameters`` method can be used to get a list
   * of possible search parameters
   * @param {number} [searchParams.limit] - page limit
   * @param {number} [searchParams.offset] - page offset
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  get(searchParams = null, timeout = 30000) {
    const req = new Request(this.auth, this.contentType, timeout);

    const updateInternalState = resp => {
      // change the state of this object on successfull response
      this.collection = null;
      this.searchParams = searchParams;

      if (resp.data && resp.data.collection) {
        this.collection = resp.data.collection;

        if (this.collection.queries && this.collection.queries.length) {
          this.queryUrl = this.collection.queries[0].href;
        }
      }
      return this;
    };

    if (searchParams) {
      for (let prop in searchParams) {
        if (searchParams.hasOwnProperty(prop) && prop !== 'limit' && prop !== 'offset') {
          // here we assume a fixed queryUrl form but API resource objecs are more flexible than this
          this.queryUrl = this.queryUrl || this.url + 'search/';

          return req.get(this.queryUrl, searchParams).then(updateInternalState);
        }
      }
      return req.get(this.url, searchParams).then(updateInternalState);
    }
    return req.get(this.url).then(updateInternalState);
  }

  /**
   * Get an array of search parameter names that can be used as properties of the
   * ``searchParams`` argument to the ``get`` method.
   *
   * @return {?string[]} - array of search parameter names or null if this list
   * resource's data has not been fetched from the API yet.
   */
  getSearchParameters() {
    if (this.collection) {
      if (this.collection.queries) {
        const params = Collection.getQueryParameters(this.collection.queries);
        params.push('limit', 'offset');
        return params;
      }
      return ['limit', 'offset'];
    }
    return null;
  }

  /**
   * Get an item resource object given its id from the list of items in this
   * list resource object.
   *
   * @param {number} id - item id
   *
   * @return {Object} - an instance of ``this.itemClass``
   */
  getItem(id) {
    if (this.isEmpty) {
      return null;
    }
    const items = this.collection.items.filter(
      item => Collection.getItemDescriptors(item).id === id
    );
    if (!items.length) {
      return null;
    }
    const itemResource = new this.itemClass(items[0].href, this.auth);
    itemResource.collection = Collection.createCollectionObj();
    itemResource.collection.items.push(items[0]);
    itemResource.collection.href = items[0].href;
    return itemResource;
  }

  /**
   * Get an array of item resource objects corresponding to the items in this
   * list resource object.
   *
   * @return {?Object[]}
   */
  getItems() {
    if (this.isEmpty) {
      return [];
    }
    return this.collection.items.map((item) => {
      const itemResource = new this.itemClass(item.href, this.auth);
      itemResource.collection = Collection.createCollectionObj();
      itemResource.collection.items.push(item);
      itemResource.collection.href = item.href;
      return itemResource;
    });
  }

  /**
   * Get the list of item data objects (REST API descriptors).
   *
   * @type {Object[]}
   */
  get data() {
    const data = [];

    if (!this.isEmpty) {
      // for each item get its data
      for (let item of this.collection.items) {
        data.push(Collection.getItemDescriptors(item));
      }
    }
    return data;
  }

  /**
   * Get the total count of items of the entire collection across pages in the
   * paginated REST API. Return -1 if no data has been fetched or the total
   * number of items info is not available from the fetched data.
   *
   * @type {number}
   */
  get totalCount() {
    if (this.collection) {
      return Collection.getTotalNumberOfItems(this.collection);
    }
    return -1;
  }

  /**
   * Return true if the list resource object has a next list page in the
   * paginated REST API.
   *
   * @type {boolean}
   */
  get hasNextPage() {
    if (this.collection) {
      const urls = Collection.getLinkRelationUrls(this.collection, 'next');
      if (urls.length) {
        return true;
      }
    }
    return false;
  }

  /**
   * Return true if the list resource object has a previous list page in the
   * paginated REST API.
   *
   * @type {boolean}
   */
  get hasPreviousPage() {
    if (this.collection) {
      const urls = Collection.getLinkRelationUrls(this.collection, 'previous');
      if (urls.length) {
        return true;
      }
    }
    return false;
  }

  /**
   * Get an array of parameter names that can be used as properties of the data
   * object in POST requests.
   *
   * @return {?string[]} - array of POST data properties or null if this list
   * resource's data has not been fetched from the API yet or it doesn't support
   * POST requests.
   */
  getPOSTParameters() {
    if (this.collection && this.collection.template) {
      return Collection.getTemplateDescriptorNames(this.collection.template);
    }
    return null;
  }

  /**
   * Internal method to fetch a related resource from the REST API that is
   * referenced by a link relation within this list resource's collection object.
   *
   * @param {string} linkRelation
   * @param {Object} ResourceClass
   * @param {Object} [searchParams=null] - search parameters object which is resource-specific
   * @param {number} [searchParams.limit] - page limit
   * @param {number} [searchParams.offset] - page offset
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to a ``ResourceClass`` object
   * @throws {RequestException} throw error if this list resource has not yet
   * been fetched from the REST API
   * @throws {RequestException} throw error when the link relation is not found
   */
  _getResource(linkRelation, ResourceClass, searchParams = null, timeout = 30000) {
    if (!this.collection) {
      throw new RequestException('Collection object has not been set!');
    }
    const urls = Collection.getLinkRelationUrls(this.collection, linkRelation);

    if (!urls.length) {
      const errMsg = 'Missing "' + linkRelation + '" link relation!';
      throw new RequestException(errMsg);
    }
    const resourceUrl = urls[0];
    const resourceObj = new ResourceClass(resourceUrl, this.auth);

    if (searchParams) {
      return resourceObj.get(searchParams, timeout);
    }
    return resourceObj.get(timeout);
  }

  /**
   * Internal helper method to make a POST request to this list resource through
   * the REST API.
   *
   * @param {Object} data - request JSON data object
   * @param {?Object} uploadFileObj - custom file object
   * @param {Object} uploadFileObj.fname - file blob
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  _post(data, uploadFileObj, timeout = 30000) {
    const url = this.url;
    const req = new Request(this.auth, this.contentType, timeout);
    let postData = data;

    if (!uploadFileObj && this.contentType === 'application/vnd.collection+json') {
      postData = { template: Collection.makeTemplate(data) };
    }

    return req.post(url, postData, uploadFileObj).then(resp => {
      // change the state of this object on successfull response
      this.collection = null;
      this.searchParams = null;
      if (resp.data && resp.data.collection) {
        this.collection = resp.data.collection;
      }
      return this;
    });
  }
}

/** * Imports ***/
import Request from './request';
import { ItemResource } from './resource';

/**
 * User item resource object representing a user of the system.
 */
export default class User extends ItemResource {
  /**
   * Constructor
   *
   * @param {string} url - url of the resource
   * @param {Object} auth - authentication object
   * @param {string} auth.token - authentication token
   */
  constructor(url, auth) {
    super(url, auth);
  }

  /**
   * Make a PUT request to modify this user item resource through the REST API.
   *
   * @param {Object} data - request JSON data object
   * @param {string} data.password - user password
   * @param {string} data.email - user email
   * @param {number} [timeout=30000] - request timeout
   *
   * @return {Object} - JS Promise, resolves to ``this`` object
   */
  put(data, timeout = 30000) {
    return this._put(data, null, timeout);
  }
}

# ChRIS Store API
JavaScript6 client for the ChRIS Store API.


## Usage

``` javascript
import Client from '@fnndsc/chrisstoreapi';

const chrisStoreUrl = 'http://localhost:8010/api/v1/';
const usersUrl = chrisStoreUrl + 'users/';
const authUrl = chrisStoreUrl + 'auth-token/';
let authToken;
let resp;


// create a new user
resp = Client.createUser(usersUrl, 'user1', 'user1pass', 'user1@gmail.com');
resp
  .then(user => {

    window.console.log('New user: ', user);
  })
  .catch(error => {

    window.console.log('Error!!!: ', error);
  });


// fetch a user auth token
resp = Client.getAuthToken(authUrl, 'cube', 'cube1234');
resp
  .then(token => {

    window.console.log('Token: ', token);
    authToken = token;
  })
  .catch(error => {

    window.console.log('Error!!!: ', error);
  });

```

The ChRIS Store Javascript client API takes an object oriented approach in which every API resource object is an instance
of a Javascript class that inherits from either of two base classes:

* ``ListResource`` (an object modeling a collection of items)
* ``ItemResource`` (an object modeling an individual item in a collection of items)

These model the two general type of REST API resources available from a ChRIS Store server.

In addition to methods and properties provided by these base classes every resource object returned by
the client API has additional methods to fetch its associated resources (item or list resources)
from the ChRIS Store REST API. It can also have ``get``, ``post``, ``put`` and ``delete`` methods as allowed by
the REST API. In particular every object inherits a ``get`` method that updates the internal state of the object
with the new data fetched from the ChRIS Store server. A `clone` method is provided to cover the case when a reference
to the previous object state is desired. This method returns a new instance of the same class as the cloned object.

All methods that fetch a resource from the REST API return a JS Promise which is passed the corresponding resource
object as an argument to the fulfillment callback.

All resources can be fetched from the REST API starting with a ``client`` object as in the following examples:


``` javascript

// create a new client instance  without an auth object to make allowed unauthenticated requests
let client = new Client(chrisStoreUrl);

// fetch a subset of the plugin metas in the store corresponding to the page determined by offset and limit
let searchParams = { limit: 20, offset:0 };

resp = client.getPluginMetas(searchParams);
resp
  .then(plgMetas => {

    window.console.log('Plugin meta list: ', plgMetas);

    // the hasNextPage/hasPreviousPage property of any list resource object informs whether
    // there is a next/previous page available from the paginated REST API
    window.console.log('Is there a next page?: ', plgMetas.hasNextPage);
    window.console.log('Is there a previous page?: ', plgMetas.hasPreviousPage);
  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });

// fetch a subset of the plugins in the store corresponding to the page determined by offset and limit
resp = client.getPlugins(searchParams);
resp
  .then(plugins => {

    window.console.log('Plugin list: ', plugins);

    // the ``data`` property of any resource object can be used to get its data
    // here we got the first page of a paginated list of data objects (plugin REST API descriptors)
    let i = 0;
    for (let dataObj of plugins.data) {
        window.console.log('Data item ' + i + ' of the plugin list resource object: ', dataObj);
        i++;
    }

    // the ``getItems`` helper method of any list resource obj can be used to create an array of
    // the individual item resource objects in the list
    // here we retrieve an array of ``Plugin`` item resource objects from ``plugins``
    const pluginArray = plugins.getItems();
    let plugin = pluginArray[0]
    window.console.log(plugin.data);

    // the ``getItem`` helper method of any list resource obj can be used to create a single item
    // resource object given it's id as long as an item with that id is in the list
    // here we create a ``Plugin`` item resource object from ``plugins``
    plugin = plugins.getItem(pluginArray[0].data.id);

    // the current object's state can be saved into another copy object before fetching
    // more data if desired
    const pluginsClone = plugins.clone();

    // the ``get`` method of any list resource obj can be used to fetch any arbitrary page
    // from the paginated REST API
    const result = plugins.get({ limit: 20, offset: 20 });
    result
      .then(plugins => {

        // the isEmpty property of any resource obj can be used to know if the object
        // contains any item/list data
        window.console.log('Does the plugin list resource object contain any data?: ', !plugins.isEmpty);
      });

  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });


// fetch a subset of the plugin metas in the store created by a specific user
let searchParams = { owner_username: 'cubeadmin', limit: 10, offset:10 };
resp = client.getPluginMetas(searchParams);
resp
  .then(userPluginMetas => {

    window.console.log('A subset of the plugin metas created by the user: ', userPluginMetas);
    window.console.log('Is there a next page?: ', userPluginMetas.hasNextPage);
  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });


// create a new client instance  with an auth object to be able to make required authenticated requests
const auth = {token: authToken}; // or alternatively auth = {username: 'cubeadmin', password: 'cubeadmin1234'}
const client = new Client(chrisStoreUrl, auth);

// fetch a subset of the plugin metas in the store that are the authenticated user's favorite
let params = { limit: 40, offset: 0 };
resp = client.getFavoritePluginMetas(params);
resp
  .then(plgMetas => {

    window.console.log('Favorite plugin meta list: ', plgMetas);
    window.console.log('Is there a next page?: ', plgMetas.hasNextPage);
  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });


// Individual item resource objects for high-level resources can also be directly  
// fetched from the REST API, these include ``PluginMeta`. ``PluginStar``, ``Plugin``,
// ``Pipeline``
// here we fetch a ``Plugin` resource object by id
const plugin_id = 1;
resp = client.getPlugin(plugin_id);
resp
  .then(plugin => {

    window.console.log('Plugin resource object: ', plugin);
    window.console.log('Data in the plugin resource object: ', plugin.data);

    // resource objects have methods to fetch other related resources
    // here we fetch the plugin-specific list of plugin parameters ``PluginParameterList``
    // resource object from the REST API
    const params = { limit: 40, offset: 0 };
    const result = plugin.getPluginParameters(params);
    result
      .then(parameters => {

        window.console.log('Plugin parameter list resource object data: ', parameters.data);
      });    
  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });


// For convenience the Client class provides a runAsyncTask static method that could
// alternatively be used to wait for promises in Javascript 6
// for instance iterate over all pages of the list of available 'fs' plugins
Client.runAsyncTask(function*() {

  const searchParams = { type: 'fs', limit: 20, offset: 0 };
  let plugins = yield client.getPlugins(searchParams); // wait for response here

  window.console.log('Page 1 data of plugin fs list resource object: ', plugins.data);

  let i = 2;
  while (plugins.hasNextPage) {

    try {
      searchParams.offset += searchParams.limit;
      plugins = yield client.getPlugins(searchParams); // wait for response here
      // alternatively, as explained above you could directly use the ``get`` method available in any resource object
      // plugins = yield plugins.get(searchParams); // wait for response here

      window.console.log('Page ' + i + ' data of plugin fs list resource object: ', plugins.data);

    } catch (ex) { // errors while fetching resources can be handled

      window.console.log('Something went wrong while fetching page ' + i '!!!: ', ex);
    }

    i++;
  }
});


// For convenience some high-level resources can be directly created through the client object
// Here the user gives a star to a plugin (making it a favorite) by creating a plugin star
const data = {
  plugin_name: 'pl-simplefsapp'  
};
resp = client.createPluginStar(data);
resp
  .then(plgStar => {

      window.console.log('New plugin star: ', plgStar);
  })
  .catch(error => {

    window.console.log('Something went wrong with this request!!!: ', error);
  });

```


## Error handling

The API basically follows the same error handling scheme as the [Axios](https://https://github.com/axios/axios)
library.

So given an error you first check if ``error.response`` exists. If so the error data is in ``error.response.data``,
otherwise `error.message` should be used to report the error.

Now ``error.response.data`` may be a plain error string or an object whose properties are the field names that produced
the bad request. It may also have a property called ``non_field_errors`` when the error is not specifically related
to a single field. The value of any of those properties is a list of plain string errors. This is the standard Django
Rest Framework approach to reporting errors.


